const Header = (props) => {
    return (
      <h1>{props.course}</h1>
    )
}

const Content = (props) => {
       const stuff = [
         {header:'Fundamentals of React', exercises:10},
         {header:'Using props to pass data', exercises:7},
         {header:'State of a component', exercises:14},
       ] 
  return (
      <>
      <p> {stuff.header[0]} {stuff.exercises[0]} </p>
      <p> {stuff.header[1]} {stuff.exercises[1]} </p>
      <p> {stuff.header[2]} {stuff.exercises[2]} </p>
      </>
  )
}


const App = () => {
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  return (
    <div>
      <Header course={'Half Stack Application Development'} />
      <Content />
      <p>Number of exercises {exercises1 + exercises2 + exercises3}</p>
    </div>
  )
}

export default App
