const App = () => {
const Header = (props) => {
    return (
      <h1>{props.course}</h1>
    )
}

const stuff = [
         {header: 'Fundamentals of React', exercises:10},
         {header: 'Using props to pass data', exercises:7},
         {header: 'State of a component', exercises:14},
       ]

const Part = (props) => {
  return (
      <p> {stuff[props.number].header} {stuff[props.number].exercises} </p>
  )
}

const Content = () => {
  return (
      <>
       <Part number={0} />
       <Part number={1} />
       <Part number={2} />
      </>
  )
}
const Total = () => {
 return( 
   <p>
    Number of exercises {stuff[0].exercises + stuff[1].exercises + stuff[2].exercises}    
  </p>
 )
}
  return (
    <div>
      <Header course={'Half Stack Application Development'} />
      <Content />
      <Total />
    </div>
  )
}

export default App
